import React, { useContext, useState } from 'react';
import { ContextAplication } from '../context/ContextAplication';

function IsEmail(name, email, input, setInput, setInformations) {
  if ((email.length !== 0) && ((email.indexOf('@') < 1) || (email.indexOf('.') < 7))) setInformations(true);
  return setInput({ ...input, [name]: email }) && setInformations(false);
}

function IsPassword(name, password, input, setInput, setInformations) {
  setInput({ ...input, [name]: password });
  if (input.password.length >= 6) return setInformations(false);
  return setInformations(true);
}

const handleChangeInput = (name, ele, input, setInput, setInformations) => {
  if (name === 'email') return IsEmail(name, ele, input, setInput, setInformations);
  return IsPassword(name, ele, input, setInput, setInformations);
};

const handleFormSubmit = (saveInput, input) => {
  saveInput(input);
};

function createForm(saveInput, input, setInput, informations, setInformations) {
  console.log(input, setInput, informations, setInformations);
  return (
    <form>
      <h2>Login</h2>
      <input
        data-testid="email-input"
        onChange={(ele) => handleChangeInput('email', ele.target.value, input, setInput, setInformations)}
        type="email"
        name="email"
      />
      <input
        data-testid="password-input"
        onChange={(ele) => handleChangeInput('password', ele.target.value, input, setInput, setInformations)}
        type="text"
        name="password"
      />
      <button
        type="button"
        disabled={informations}
        data-testid="login-submit-btn"
        onClick={handleFormSubmit(saveInput, input)}
      >
        Entrar
      </button>
    </form>
  );
}

function LoginScreen() {
  const { saveInput } = useContext(ContextAplication);
  const [input, setInput] = useState({ email: '', password: '' });
  const [informations, setInformations] = useState(true);

  return (
    <div>
      {createForm(saveInput, input, setInput, informations, setInformations)}
    </div>
  );
}

export default LoginScreen;
