import React, { useState } from 'react';
// import { ContextAplication } from '../context/ContextAplication';

function verifyUserInputs(name, event, input, setInput, setInformations) {
  let email = false;
  setInput({ ...input, [name]: event });

  if (name === 'email') {
    if ((event.length !== 0) && ((event.indexOf('@') < 1) || (event.indexOf('.') < 7))) email = true;
    if (input.password.length >= 6 && email) return setInformations(false);
  }

  if (name === 'password') {
    // if (input.password.length < 6) return setInformations(true);
  }

  return null;
}

const handleChangeInput = (name, event, input, setInput, setInformations) => {
  verifyUserInputs(name, event, input, setInput, setInformations);
};

// const handleFormSubmit = (saveInput, input) => {
//   saveInput(input);
// };

function createForm(input, setInput, informations, setInformations) {
  console.log(input, setInput, informations, setInformations);
  return (
    <form>
      <h2>Login</h2>
      <input
        data-testid="email-input"
        onChange={(ele) => handleChangeInput('email', ele.target.value, input, setInput, setInformations)}
        type="email"
        name="email"
      />
      <input
        data-testid="password-input"
        onChange={(ele) => handleChangeInput('password', ele.target.value, input, setInput, setInformations)}
        type="text"
        name="password"
      />
      <button
        type="button"
        disabled={informations}
        data-testid="login-submit-btn"
        // onClick={handleFormSubmit(saveInput, input)}
      >
        Entrar
      </button>
    </form>
  );
}

function LoginScreen() {
  // const { saveInput, informationsUser } = useContext(ContextAplication);
  const [input, setInput] = useState({ email: '', password: '' });
  const [informations, setInformations] = useState(true);
  return (
    <div>
      {createForm(input, setInput, informations, setInformations)}
    </div>
  );
}

export default LoginScreen;
