import React from 'react';
import profileIcon from '../images/profileIcon.svg';
import '../components/CSS/DoneRecipes.css';
import shareIcon from '../images/shareIcon.svg';

const recipesDone = JSON.parse(localStorage.getItem('doneRecipes'));

const header = () => {
  return (
    <div className="header-recipes-done">
      <img
        src={profileIcon}
        alt="ProfileIcon"
        data-testeid="profile-top-btn"
      />
      <p>Receitas Feitas</p>
    </div>
  );
};

const renderRecipes = () => {
  recipesDone.map(({ image, id, name, category }) => {
    return (
      <div>
        <img
          data-testid="filter-by-drink-btn"
          src={image}
          alt="Imagem da Receita Finalizada"
        />
        <img
          data-testid="filter-by-drink-btn"
          src={shareIcon}
          alt="Icone de Compartilhamento"
        />
        <p data-testid={`${id}-horizontal-name`}>{name}</p>
        <p data-testid={`${id}-horizontal-done-date`}>{id}</p>
        <p data-testid={`${id}-${name}-horizontal-tag`}>{category}</p>
        <p data-testid={`${id}-${name}-horizontal-tag`}>{category}</p>
      </div>
    );
  });
};

const buttons = () => {
  return (
    <div className="test">
      <button
        type="button"
        className="buttons-filter"
        data-testid="filter-by-all-btn"
        onClick={renderRecipes}
      >
        All
      </button>
      <button
        type="button"
        className="buttons-filter"
        data-testid="filter-by-food-btn"
      >
        Food
      </button>
      <button
        type="button"
        className="buttons-filter"
        data-testid="filter-by-drink-btn"
      >
        Drinks
      </button>
    </div>
  );
};

function DoneRecipes() {
  console.log(recipesDone);
  return (
    <div>
      {header()}
      {buttons()}
    </div>
  );
}

export default DoneRecipes;
