import React, { useState, useEffect, useContext } from 'react';
import { ContextAplication } from '../context/ContextAplication';

const handleChangeInput = (name, event, input, setInput) => {
  setInput({ ...input, [name]: event });
};

// const handleFormSubmit = (saveInput, input) => {
//   saveInput(input);
// };

function createForm(input, setInput, informations, setInformations) {
  console.log(input, setInput, informations, setInformations);
  return (
    <form>
      <h2>Login</h2>
      <input
        data-testid="email-input"
        onChange={(ele) => handleChangeInput('email', ele.target.value, input, setInput)}
        type="email"
        name="email"
      />
      <input
        data-testid="password-input"
        onChange={(ele) => handleChangeInput('password', ele.target.value, input, setInput)}
        type="text"
        name="password"
      />
    </form>
  );
}

function LoginScreen() {
  const { saveInput, informationsUser } = useContext(ContextAplication);
  const [input, setInput] = useState({ email: '', password: '' });
  const [informations, setInformations] = useState(true);

  useEffect(() => {
    if (
      (
        (input.email.length !== 0)
        && ((input.email.indexOf('@') < 1)
        || (input.email.indexOf('.') < 7))
      )
      || (input.password.length < 6)
    ) return setInformations(true);
    return setInformations(false);
  }, [input]);

  return (
    <div>
      {createForm(input, setInput, informations)}
      <button
        type="button"
        disabled={informations}
        data-testid="login-submit-btn"
        onClick={() => saveInput(input)}
      >
        Entrar
      </button>
    </div>
  );
}

export default LoginScreen;
