import React, { useState, useEffect } from 'react';
import CopyToClipboard from 'react-copy-to-clipboard';

import profileIcon from '../images/profileIcon.svg';
import shareIcon from '../images/shareIcon.svg';
import '../components/CSS/DoneRecipes.css';

const header = () => {
  return (
    <div className="header-recipes-done">
      <img
        src={profileIcon}
        alt="ProfileIcon"
        data-testeid="profile-top-btn"
      />
      <p>Receitas Feitas</p>
    </div>
  );
};

const buttons = (recipes, setRecipes) => {
  return (
    <div className="test">
      <button
        type="button"
        className="buttons-filter"
        data-testid="filter-by-all-btn"
        onClick={() => setRecipes(recipes)}
      >
        All
      </button>
      <button
        type="button"
        className="buttons-filter"
        data-testid="filter-by-food-btn"
        onClick={() => setRecipes()}
      >
        Food
      </button>
      <button
        type="button"
        className="buttons-filter"
        data-testid="filter-by-drink-btn"
        onClick={() => setRecipes()}
      >
        Drinks
      </button>
    </div>
  );
};

const renderRecipes = (recipes) => {
  console.log(recipes);
  return (
    <div>
      {recipes.map(({ image, id, name, category }) => {
        return (
          <div className="recipes-done" key={id}>
            <img
              data-testid="filter-by-drink-btn"
              src={image}
              alt="Imagem da Receita Finalizada"
              width="150px"
            />
            <p data-testid={`${id}-horizontal-name`}>{name}</p>
            <p data-testid={`${id}-horizontal-done-date`}>{id}</p>
            <p data-testid={`${id}-${name}-horizontal-tag`}>{category}</p>
            <p data-testid={`${id}-${name}-horizontal-tag`}>{category}</p>
            <CopyToClipboard text={window.location.href}>
              <button
                className="share"
                type="button"
                data-testid="filter-by-drink-btn"
                onClick={() => alert('Link copiado!')}
              >
                <img src={shareIcon} alt="icon" />
              </button>
            </CopyToClipboard>
          </div>
        );
      })}
    </div>
  );
};

function DoneRecipes() {
  const [recipes, setRecipes] = useState(JSON.parse(localStorage.getItem('doneRecipes')));
  const state = recipes;
  useEffect(() => {
    renderRecipes(recipes);
  }, [recipes]);
  return (
    <div>
      {header()}
      {buttons(recipes, setRecipes, state)}
      {recipes && renderRecipes(recipes)}
    </div>
  );
}

export default DoneRecipes;
